// Simple jQuery event handler
$(document).ready(function () {
    "use strict";
   $(window).scroll(function() {
    if ($(window).scrollTop() > 10) {
        $('#navBar').addClass('floatingNav');
    } else {
        $('#navBar').removeClass('floatingNav');
    }
});
});